mapper.insert:
insert into TB_KEYWORD (keyword_Feature, keyword_name, 
  add_UserId, add_Date, rcd_UserId, 
  rcd_Date, keyword_IsEnable
  )
values (#{keywordFeature,jdbcType=VARCHAR}, #{keywordName,jdbcType=VARCHAR}, 
  #{addUserid,jdbcType=BIGINT}, #{addDate,jdbcType=TIMESTAMP}, #{rcdUserid,jdbcType=BIGINT}, 
  #{rcdDate,jdbcType=TIMESTAMP}, #{keywordIsenable,jdbcType=INTEGER}
  )

mapper.select:
    select 

    from TB_KEYWORD
    where    keyword_IsEnable = #{keywordIsenable,jdbcType=VARCHAR}   

     and   keyword_Feature = #{keywordFeature,jdbcType=VARCHAR}

	order by rcd_Date

select     keyword_Feature
from TB_KEYWORD

 where   keyword_IsEnable = #{keywordIsenable,jdbcType=INTEGER}

mapper.update:
update TB_KEYWORD
set rcd_UserId = #{rcdUserid},
  rcd_Date = #{rcdDate},
  keyword_IsEnable = 0 
where keyword_Feature = #{keywordFeature} 

 and  keyword_IsEnable = #{keywordIsenable,jdbcType=INTEGER}

update TB_KEYWORD
set rcd_UserId = #{rcdUserid},
  rcd_Date = #{rcdDate},
  keyword_IsEnable = 0 
where keyword_Feature = #{keywordFeature} and keyword_name = #{keywordName}

 and  keyword_IsEnable = #{keywordIsenable,jdbcType=INTEGER}
